import React, { Fragment, useState } from 'react'

const InputTodo = () => {

  const [description, setDestription] = useState("hello");

  return (
    <Fragment className=''>
      <h1 className='text-center mt-5 text-4xl font-bold block'>Pern Todo List</h1>
      <form>
        <div className="w-[75%] text-center mx-auto mt-5">
          <input type="text" className='border-2 border-black  w-[400px] mx-auto h-8 px-2 py-5' value={description} onChange={e=> setDestription}/>
          <a href="#" className='inline-block border-2 border-black rounded-xl py-2 bg-gray-300 duration-200 hover:bg-gray-500 w-24 ml-5'>Add</a>
        </div>
      </form>
    </Fragment>
  )
}

export default InputTodo
